const rota = require('../modal/Rota')


exports.criarTrajeto = async (markers = [], nav, AsyncStorage, Alert) =>{
    let lt = [markers[0].longitude, markers[0].latitude];
    let rt = [markers[1].longitude, markers[1].latitude];
    let lb = [markers[2].longitude, markers[2].latitude];
    let rb = [markers[3].longitude, markers[3].latitude];
  
    let retorno = await rota.cadastrarRota(lt, rt, lb, rb, AsyncStorage);
    if(retorno.message == "Rota criada."){
        Alert.alert(
            "Sucesso",
            "Rota Criada com Sucesso",
            [
              { text: "OK", onPress: () => nav.navigate("main") }
            ]);
    }
    else{
        Alert.alert(
            "Erro",
            retorno.message,
            [
              { text: "OK", onPress: () => console.log("OK Pressed") }
            ]);
    }
    
}
